$(function(){$(".js-pomodoro").click(function(){$("#timer_default").createTimer({time_in_seconds:1500,autostart:!0})}),$(".js-short-break").click(function(){$("#timer_default").createTimer({time_in_seconds:300,autostart:!0})}),$(".js-long-break").click(function(){$("#timer_default").createTimer({time_in_seconds:600,autostart:!0})}),$("#timer_toggle").click(function(){"running"==$("#timer_default").data("countdown.state")?$("#timer_default").pauseTimer():$("#timer_default").startTimer()}),$("#timer_reset").click(function(){$("#timer_default").resetTimer()})});